import React, { useState } from "react";
import { useSpring, animated } from 'react-spring';

function Quote() {
    const [toggleEdit, setToggleEdit] = useState(false);

    function toggleEditor(){
        console.log("TOGGLED");
        if(toggleEdit === true){
            setToggleEdit(false);
        }
        else if(toggleEdit === false){
            setToggleEdit(true);
        }
    }

    //useSpring animations
    const openEditor = useSpring({
        height: "75vh",

        from: {
            height: "10vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const closeEditor = useSpring({
        height: "10vh",

        from: {
            height: "75vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    return(
        <animated.div style={toggleEdit ? openEditor : closeEditor} className="quote-list-container">
            <div className="info">
                <h3>Truck Info</h3>
            </div>

            <div className="mini-display">
                <h2>Nolasco, Ever</h2>
                <div className="quote-list-form">
                    <button onClick = {toggleEditor} className="edit-button">{toggleEdit ? "Close" : "View"}</button>
                    <button className="delete-button">Delete</button>
                </div>
            </div>
        </animated.div>
    );
}

export default Quote;
