import React from "react";

function DriverInfo({key, id, fName, lName, age, driverInfo, setDriverInfo, postForm, setPostForm}){
    //function that will remove current driver field from state
    function deleteDriverField(){
        setDriverInfo(driverInfo.filter((element) => element.id !== id))
    }

    //change the value of the driverInfo state and the input text box
    function inputTextHandler(e){
        setPostForm((prevState) => ({...prevState, [e.target.name+key]: e.target.value}))
    }

    return(
        <div className="driver-info">
            <div className="input-box">
                <p>First Name</p>
                <input type="text" style={{width: "200px"}} name={"fName"+key} value={fName} onChange={inputTextHandler}/>
            </div>

            <div className="input-box">
                <p>Last Name</p>
                <input type="text" style={{width: "200px"}} name={"lName"+key} value={lName}/>
            </div>

            <div className="input-box">
                <p>Age</p>
                <input type="text" style={{width: "100px"}} name={"age"+key} value={age}/>
            </div>
            <button className="x-button" onClick={deleteDriverField}>X</button>
        </div>
    );
}

export default DriverInfo;
