import React, { useState } from "react";
import { useSpring, animated } from 'react-spring';

function Quote() {
    const [toggleEdit, setToggleEdit] = useState(false);

    function toggleEditor(){
        console.log("TOGGLED");
        if(toggleEdit === true){
            setToggleEdit(false);
        }
        else if(toggleEdit === false){
            setToggleEdit(true);
        }
    }

    return(
        <animated.div style={toggleEdit === true ? openEditor : closeEditor} className="quote-list-container">
            <h2>Nolasco, Ever</h2>
            <div className="quote-list-form">
                <button onClick = {toggleEditor} className="edit-button">Edit</button>
                <button className="delete-button">Delete</button>
            </div>
        </animated.div>
    );

    //useSpring animations
    const openEditor = useSpring({
        height: "75vh",
        alignItems: "flex-end",

        from: {
            height: "10vh",
            alignItems: "center"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const closeEditor = useSpring({
        height: "10vh",
        alignItems: "center",

        from: {
            height: "75vh",
            alignItems: "flex-end"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })
}

export default Quote;
