import React, { useState } from "react";
import { useSpring, animated } from 'react-spring';

function Quote() {
    const [toggleEdit, setToggleEdit] = useState(false);

    function toggleEditor(){
        console.log("TOGGLED");
        if(toggleEdit === true){
            setToggleEdit(false);
        }
        else if(toggleEdit === false){
            setToggleEdit(true);
        }
    }

    //useSpring animations
    const openEditor = useSpring({
        height: "75vh",

        from: {
            height: "10vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const closeEditor = useSpring({
        height: "10vh",

        from: {
            height: "75vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    return(
        <animated.div style={toggleEdit ? openEditor : closeEditor} className="quote-list-container">
            <form className="info">
                <h3>Truck Info</h3>
                <div className="truck-info">
                    <div className="input-box">
                        <p>Dot Number</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Premium Amount</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p># of Power Units</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Premium Tax</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Value of Power Units</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Premium Total</p>
                        <input type="text"/>
                    </div>
                </div>

                <h3>Address</h3>
                <div className="address-info">
                    <div className="input-box">
                        <p>Street</p>
                        <input className="address-input" type="text"/>
                    </div>

                    <div className="input-box">
                        <p>City</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>State</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Zip Code</p>
                        <input type="text"/>
                    </div>
                </div>
            </form>

            <div className="mini-display">
                <h2>Nolasco, Ever</h2>
                <div className="quote-list-form">
                    <button onClick = {toggleEditor} className="edit-button">{toggleEdit ? "Close" : "View"}</button>
                    <button className="delete-button">Delete</button>
                </div>
            </div>
        </animated.div>
    );
}

export default Quote;
