export default (state = {authData: null}, action) => {
    switch(action.type){
        case 'AUTH':
            //get the profile info and set it to local storage
            localStorage.setItem('user', JSON.stringify({...action?.data}));
            return {...state, authData: action?.data};
        default:
            return state;
    }
}
