import React, { useState, useEffect } from "react";
import {useSpring, animated} from 'react-spring';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';

//import components
import Quote from "./quote";
import QuoteForm from "./quoteForm";

function QuotesPage({driverInfo, setDriverInfo}){
    const quotes = useSelector((state) => state.posts);
    const dispatch = useDispatch();
    console.log("THESE QUOTES: ", quotes);

    useEffect(() => {
        dispatch(getPosts());
    }, [dispatch])

    //states to toggle the form on and off, and to input information to the form
    const [toggleAdd, setToggleAdd] = useState(false);
    const [postForm, setPostForm] = useState([]);

    //arrays that hold field names for driver name and age. Used when adding a variable to driverInfo array
    var names = ["name1", "name2", "name3", "name4", "name5"];
    var ages = ["age1", "age2", "age3", "age4", "age5"];

    function toggleAddForm(){
        if(toggleAdd){
            setToggleAdd(false);
        }
        else{
            setToggleAdd(true);
        }
    }

    function addDriverField(e){
    e.preventDefault(); // prevents page from reloading
      setDriverInfo([
        ...driverInfo, {fName: "", lName: "", age: "", id: Math.random()*1000},
      ]);

      console.log("DRIVERINFO: ", driverInfo);
    }

    //Animation to open and close the 'Add a Quote' form
    const openAddForm = useSpring({
        height: "85vh",
        opacity: 1,

        from: {
            height: "0vh",
            opacity: 0
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const closeAddForm = useSpring({
        height: "0vh",
        opacity: 0,

        from: {
            height: "85vh",
            opacity: 1
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    return(
        <div>
            <header>
                <form className="log-out-form">
                    <button>Log Out</button>
                </form>

                <div className="title-line">
                    <h1>Ever's Quotes</h1>
                    <button onClick={toggleAddForm} style={toggleAdd ? {backgroundColor: "#E84855"} : null}>{toggleAdd ? "Discard" : "Add a Quote"}</button>
                </div>
            </header>

            <animated.form style={toggleAdd ? openAddForm : closeAddForm} className="add-quote-form">
                <QuoteForm
                    postForm={postForm}
                    setPostForm={setPostForm}
                />
            </animated.form>

            <Quote/>
            <Quote/>
            <Quote/>


        </div>
    );
}

export default QuotesPage;
