import React from "react";

function DriverInfo({key, id, fName, lName, age, driverInfo, setDriverInfo, postForm, setPostForm}){
    //function that will remove current driver field from state
    function deleteDriverField(){
        setDriverInfo(driverInfo.filter((element) => element.id !== id))
    }

    //change the value of the driverInfo state and the input text box
    function inputNameHandler(e){
        setDriverInfo(driverInfo => {const name = [...name, e.target.value]})
    }

    function inputAgeHandler(e){
        setDriverInfo(driverInfo => {const age = [...age, e.target.value]})
    }

    return(
        <div className="driver-info">
            <div className="input-box">
                <p>Name</p>
                <input type="text" style={{width: "200px"}} name="fName" value={driverInfo.fName} onChange={inputNameHandler}/>
            </div>

            <div className="input-box">
                <p>Age</p>
                <input type="text" style={{width: "100px"}} name="age" value={driverInfo.age} onChange={inputAgeHandler}/>
            </div>
            <button className="x-button" onClick={deleteDriverField}>X</button>
        </div>
    );
}

export default DriverInfo;
