import React, { useState } from "react";
import {useSpring, animated} from 'react-spring';

//import components
import Quote from "./quote";
import DriverInfo from "./driverInfo";

function QuotesPage({driverInfo, setDriverInfo}){
    //states to toggle the form on and off, and to input information to the form
    const [toggleAdd, setToggleAdd] = useState(false);
    const [postForm, setPostForm] = useState([]);

    function toggleAddForm(){
        if(toggleAdd){
            setToggleAdd(false);
        }
        else{
            setToggleAdd(true);
        }
    }

    function addDriverField(e){
    e.preventDefault(); // prevents page from reloading
      setDriverInfo([
        ...driverInfo, {fName: "", lName: "", age: "", id: Math.random()*1000},
      ]);
    }

    function inputTextHandler(e){
        setPostForm((prevState) => ({...prevState, [e.target.name]: e.target.value}))
    }

    //Animation to open and close the 'Add a Quote' form
    const openAddForm = useSpring({
        height: "85vh",
        opacity: 1,

        from: {
            height: "0vh",
            opacity: 0
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const closeAddForm = useSpring({
        height: "0vh",
        opacity: 0,

        from: {
            height: "85vh",
            opacity: 1
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    return(
        <div>
            <header>
                <form className="log-out-form">
                    <button>Log Out</button>
                </form>

                <div className="title-line">
                    <h1>Ever's Quotes</h1>
                    <button onClick={toggleAddForm} style={toggleAdd ? {backgroundColor: "#E84855"} : null}>{toggleAdd ? "Discard" : "Add a Quote"}</button>
                </div>
            </header>

            <animated.form style={toggleAdd ? openAddForm : closeAddForm} className="add-quote-form">
                <h3>Truck Info</h3>
                <div className="truck-info">
                    <div className="input-box">
                        <p>Dot Number</p>
                        <input type="text" name="dotNum" value={postForm.dotNum} onChange={inputTextHandler}/>
                    </div>

                    <div className="input-box">
                        <p>Premium Amount</p>
                        <input type="text" name="premAmnt" value={postForm.premAmnt} onChange={inputTextHandler}/>
                    </div>

                    <div className="input-box">
                        <p># of Power Units</p>
                        <input type="text" name="pwrNum" value={postForm.pwrNum} onChange={inputTextHandler}/>
                    </div>

                    <div className="input-box">
                        <p>Premium Tax</p>
                        <input type="text" name="premTax" value={postForm.premTax} onChange={inputTextHandler}/>
                    </div>

                    <div className="input-box">
                        <p>Value of Power Units</p>
                        <input type="text" name="pwrVal" value={postForm.pwrVal} onChange={inputTextHandler}/>
                    </div>

                    <div className="input-box">
                        <p>Premium Total</p>
                        <input type="text" name="premTot" value={postForm.premTot} onChange={inputTextHandler}/>
                    </div>
                </div>

                <h3 style={{marginTop: "25px"}}>Address</h3>
                <div className="driver-info">
                    <div className="input-box">
                        <p>Street</p>
                        <input className="address-input" type="text" style={{width: "500px"}} name="street" value={postForm.street} onChange={inputTextHandler}/>
                    </div>

                    <div className="input-box">
                        <p>City</p>
                        <input type="text" style={{width: "200px"}} name="city" value={postForm.city} onChange={inputTextHandler}/>
                    </div>

                    <div className="input-box">
                        <p>State</p>
                        <input type="text" style={{width: "100px"}} name="state" value={postForm.state} onChange={inputTextHandler}/>
                    </div>

                    <div className="input-box">
                        <p>Zip Code</p>
                        <input type="text" style={{width: "100px"}} name="zip" value={postForm.zip} onChange={inputTextHandler}/>
                    </div>
                </div>

                <h3 style={{marginTop: "25px"}}>Driver Info</h3>
                {driverInfo.map((item) => (
                    <DriverInfo
                        key={item.id}
                        id={item.id}
                        fName={item.fName}
                        lName={item.lName}
                        age={item.age}
                        driverInfo={driverInfo}
                        setDriverInfo={setDriverInfo}
                        postForm={postForm}
                    />
                ))}

                <button onClick={addDriverField} style={driverInfo.length >= 5 ? {display: "none"} : null}>Add Driver</button>

                <input className="updateInfo" type="submit" value="Add Quote"/>
            </animated.form>

            <Quote/>
            <Quote/>
            <Quote/>


        </div>
    );
}

export default QuotesPage;
