import React, { useState } from "react";
import { useSpring, animated } from "react-spring";

function LoginPage(){
    const [formExpand, setFormExpand] = useState(false);

    //Animate form box
    const expandForm = useSpring({
        height: "60vh",

        from: {
            height: "25vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const reduceForm = useSpring({
        height: "25vh",

        from: {
            height: "60vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const fadeInForm = useSpring({
        opacity: 1,

        from: {
            opacity: 0
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const fadeOutForm = useSpring({
        opacity: 0,

        from: {
            opacity: 1
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    return(
        <div className="login-page">
            <h1>Fake Insurance Company</h1>

            <animated.div className="form" style={formExpand ? expandForm : reduceForm}>
                <h3>{formExpand ? "Create Account" : "Log In"}</h3>

                <form>
                    <input type="text" placeholder="Email"/>
                    <input type="password" placeholder="Password"/>
                    <input type="submit" value="Log In"/>
                </form>

                <form>
                    <input type="text" placeholder="Full Name"/>
                    <input type="text" placeholder="Email"/>
                    <input type="text" placeholder="Company Name"/>
                    <input type="text" placeholder="Street"/>
                    <input type="text" placeholder="City"/>
                    <input type="text" placeholder="Zip Code"/>
                    <input type="password" placeholder="Create password"/>
                    <input type="password" placeholder="Confirm password"/>
                    <input type="submit" value="Sign Up"/>
                </form>
            </animated.div>

            <button to="/create_account" className="secondary-button">{formExpand ? "Log In" : "Sign Up"}</button>
        </div>
    );
}

export default LoginPage;
