import React, { useState } from "react";
import { useSpring, animated } from "react-spring";

function LoginPage({createUser, setCreateUser}){
    const [formExpand, setFormExpand] = useState(false);
    const [doAnimate, setDoAnimate] = useState(false);

    //update the createUser state
    function inputTextHandler(e){
        e.preventDefault();
        setCreateUser((prevState) => ({...prevState, [e.target.name]: e.target.value}));
    }

    //toggle the form animation
    function toggleForm(){
        if(formExpand){
            setFormExpand(false);
        }
        else{
            setFormExpand(true);
        }
    }

    //Animate form box
    const expandForm = useSpring({
        height: "60vh",

        from: {
            height: "25vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const reduceForm = useSpring({
        height: "25vh",

        from: {
            height: "60vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const fadeInForm = useSpring({
        opacity: 1,
        zIndex: "10",

        from: {
            opacity: 0,
            zIndex: "1"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const fadeOutForm = useSpring({
        opacity: 0,
        zIndex: "1",

        from: {
            opacity: 1,
            zIndex: "10"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    return(
        <div className="login-page">
            <h1>Fake Insurance Company</h1>

            <animated.div className="form" style={formExpand ? expandForm : reduceForm}>
                <h3>{formExpand ? "Create Account" : "Log In"}</h3>

                <animated.form style={formExpand ? fadeOutForm : fadeInForm}>
                    <input type="text" placeholder="Email" onChange={inputTextHandler}/>
                    <input type="password" placeholder="Password" onChange={inputTextHandler}/>
                    <input className="primary-button" type="submit" value="Log In"/>
                </animated.form>

                <animated.form style={formExpand && doAnimate ? fadeInForm : fadeOutForm}>
                    <input type="text" placeholder="Full Name" name="name" value={createUser.name} onChange={inputTextHandler}/>
                    <input type="text" placeholder="Email" name="email" value={createUser.email} onChange={inputTextHandler}/>
                    <input type="text" placeholder="Company Name" name="company_name" value={createUser.company_name} onChange={inputTextHandler}/>
                    <input type="text" placeholder="Street" name="company_street" value={createUser.company_street} onChange={inputTextHandler}/>
                    <input type="text" placeholder="City" name="company_city" value={createUser.company_city} onChange={inputTextHandler}/>
                    <input type="text" placeholder="Zip Code" name="company_zipcode" value={createUser.company_zipcode} onChange={inputTextHandler}/>
                    <input type="password" placeholder="Create password" name="password" value={createUser.password} onChange={inputTextHandler}/>
                    <input type="password" placeholder="Confirm password"/>
                    <input className="primary-button" type="submit" value="Sign Up"/>
                </animated.form>
            </animated.div>

            <button to="/create_account" className="secondary-button" onClick={toggleForm}>{formExpand ? "Log In" : "Sign Up"}</button>
        </div>
    );
}

export default LoginPage;
