import React from "react";

function DriverInfo({id, fName, lName, age, driverInfo, setDriverInfo, postForm}){
    //function that will remove current driver field from state
    function deleteDriverField(){
        setDriverInfo(driverInfo.filter((element) => element.id !== id))
    }

    return(
        <div className="driver-info">
            <div className="input-box">
                <p>First Name</p>
                <input type="text" style={{width: "200px"}}/>
            </div>

            <div className="input-box">
                <p>Last Name</p>
                <input type="text" style={{width: "200px"}}/>
            </div>

            <div className="input-box">
                <p>Age</p>
                <input type="text" style={{width: "100px"}}/>
            </div>
            <button className="x-button" onClick={deleteDriverField}>X</button>
        </div>
    );
}

export default DriverInfo;
