import React, { useState } from "react";
import { useSpring, animated } from 'react-spring';

function Quote() {
    const [toggleEdit, setToggleEdit] = useState(false);

    function toggleEditor(){
        console.log("TOGGLED");
        if(toggleEdit === true){
            setToggleEdit(false);
        }
        else if(toggleEdit === false){
            setToggleEdit(true);
        }
    }

    //useSpring animations
    const openEditor = useSpring({
        height: "75vh",

        from: {
            height: "10vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const closeEditor = useSpring({
        height: "10vh",

        from: {
            height: "75vh"
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const displayForm = useSpring({
        opacity: 1,

        from: {
            opacity: 0
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const hideForm = useSpring({
        opacity: 0,

        from: {
            opacity: 1
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    return(
        <animated.div style={toggleEdit ? openEditor : closeEditor} className="quote-list-container">
            <form className="info">
                <h3>Truck Info</h3>
                <div className="truck-info">
                    <div className="input-box">
                        <p>Dot Number</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Premium Amount</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p># of Power Units</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Premium Tax</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Value of Power Units</p>
                        <input type="text"/>
                    </div>

                    <div className="input-box">
                        <p>Premium Total</p>
                        <input type="text"/>
                    </div>
                </div>

                <h3 style={{marginTop: "25px"}}>Address</h3>
                <div className="driver-info">
                    <div className="input-box">
                        <p>Street</p>
                        <input className="address-input" type="text" style={{width: "500px"}}/>
                    </div>

                    <div className="input-box">
                        <p>City</p>
                        <input type="text" style={{width: "200px"}}/>
                    </div>

                    <div className="input-box">
                        <p>State</p>
                        <input type="text" style={{width: "100px"}}/>
                    </div>

                    <div className="input-box">
                        <p>Zip Code</p>
                        <input type="text" style={{width: "100px"}}/>
                    </div>
                </div>

                <h3 style={{marginTop: "25px"}}>Driver Info</h3>
                <div className="driver-info">
                    <div className="input-box">
                        <p>First Name</p>
                        <input type="text" style={{width: "200px"}}/>
                    </div>

                    <div className="input-box">
                        <p>Last Name</p>
                        <input type="text" style={{width: "200px"}}/>
                    </div>

                    <div className="input-box">
                        <p>Age</p>
                        <input type="text" style={{width: "100px"}}/>
                    </div>
                </div>
            </form>

            <div className="mini-display">
                <h2>Nolasco, Ever</h2>
                <div className="quote-list-form">
                    <button onClick = {toggleEditor} className="edit-button">{toggleEdit ? "Close" : "View"}</button>
                    <button className="delete-button">Delete</button>
                </div>
            </div>
        </animated.div>
    );
}

export default Quote;
