import React, { useState } from "react";
import {useSpring, animated} from 'react-spring';
import { useSelector } from 'react-redux';

//import components
import Quote from "./quote";
import QuoteForm from "./quoteForm";

function QuotesPage(){
    const posts = useSelector((state) => state.posts);
    console.log("THESE QUOTES: ", posts[9].driver2_name);

    //states to toggle the form on and off, and to input information to the form
    const [toggleAdd, setToggleAdd] = useState(false);

    //initialize postForm
    const [postForm, setPostForm] = useState({
        dot_number: "",
        nbr_of_pwr_units: "",
        val_of_pwr_units: "",
        premium_amount: "",
        premium_tax: "",
        premium_total: "",
        street: "",
        city: "",
        state: "",
        zipcode: "",
        driver1_name: "",
        driver1_age: "",
        driver2_name: "",
        driver2_age: "",
        driver3_name: "",
        driver3_age: "",
        driver4_name: "",
        driver4_age: "",
        driver5_name: "",
        driver5_age: ""
    });

    function toggleAddForm(){
        if(toggleAdd){
            setToggleAdd(false);
        }
        else{
            setToggleAdd(true);
        }
    }

    //Animation to open and close the 'Add a Quote' form
    const openAddForm = useSpring({
        height: "85vh",
        opacity: 1,

        from: {
            height: "0vh",
            opacity: 0
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    const closeAddForm = useSpring({
        height: "0vh",
        opacity: 0,

        from: {
            height: "85vh",
            opacity: 1
        },

        config: {
            mass: 1,
            tension: 170,
            friction: 20
        },
        reset: true
    })

    return(
        <div>
            <header>
                <form className="log-out-form">
                    <button>Log Out</button>
                </form>

                <div className="title-line">
                    <h1>Ever's Quotes</h1>
                    <button onClick={toggleAddForm} style={toggleAdd ? {backgroundColor: "#E84855"} : null}>{toggleAdd ? "Discard" : "Add a Quote"}</button>
                </div>
            </header>

            <animated.form style={toggleAdd ? openAddForm : closeAddForm} className="add-quote-form">
                <QuoteForm
                    postForm={postForm}
                    setPostForm={setPostForm}
                />
            </animated.form>

            <Quote/>
            <Quote/>
            <Quote/>


        </div>
    );
}

export default QuotesPage;
